import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { setSettings, setWidgetsList } from "../../redux/actions/app_action";

import ImgMenu from "../../components/WidgetsToolBar/images/menu-icon.png";
import ResizibleImg from "../../components/WidgetsToolBar/images/resizible.png";

import styled from "styled-components";

import Grid from "../../images/grid.png";

// DND

import { useDrop } from "react-dnd";
import ReactResizeDetector from "react-resize-detector";

// LODASH

import get from "lodash/get";
import { makeResizableDiv } from "./helpers/makeResizibleDiv";
import DragableWgComponent from "./views/DragableWgComponent";

const DnDBox = styled.div`
  width: calc(100% - 60px);
  margin: 0 60px 0 0;
  height: calc(100vh - 60px);
  display: flex;
  flex-flow: nowrap column;
  overflow: auto;

  .react-draggable,
  .cursor {
    cursor: move;
  }
  .no-cursor {
    cursor: auto;
  }
  .cursor-y {
    cursor: ns-resize;
  }
  .cursor-x {
    cursor: ew-resize;
  }

  .box {
    min-height: 200px;
    min-width: 200px;
    float: left;
    z-index: 999;

    &:hover {
      cursor: default;
    }

    //&::-webkit-resizer {
    //}
  }

  .sidebar {
    border-radius: 2px;
    position: relative;
    z-index: 1000;
    resize: both;
    overflow: auto;
    box-sizing: border-box;
  }

  .wg-info {
    text-align: center;
    padding: 10px;
    background: #fff;
  }

  .close-btn {
    display: flex;
    justify-content: center;
    align-items: center;
    font-size: 12px;
    position: absolute;
    top: -15px;
    right: -15px;
    width: 30px;
    height: 30px;
    border-radius: 50%;
    border: 1px solid #ccc;
    background: #fff;
    transition: 0.3s;
    &:hover {
      cursor: pointer;
      transition: 0.3s;
      background: #4da6ff;
    }
    &:focus {
      outline: none;
    }
  }
  .remove-btn {
    margin: 5px 0 0 0;
    padding: 5px;
    border: none;
    box-shadow: 0 0 3px 0 rgba(0, 0, 0, 0.8);
    background: #fff;
    transition: 0.3s;
    &:hover {
      transition: 0.3s;
      cursor: pointer;
      color: crimson;
    }
    &:focus {
      outline: none;
    }
  }

  .resizible-btn {
    position: absolute;
    height: 50px;
    width: 50px;
    background: #fff url(${ResizibleImg}) no-repeat center;
    background-size: 50%;
    border-radius: 50%;
    left: -10px;
    top: -10px;
    z-index: 100;
    box-shadow: 0 0 3px 0 rgba(0, 0, 0, 0.8);
    transition: 0.3s;
    &:hover {
      transition: 0.3s;
      cursor: move;
      background: #4da6ff url(${ResizibleImg}) no-repeat center;
      background-size: 50%;
      transform: rotate(360deg);
    }
  }

  body,
  html {
    background: black;
  }
  .resizable {
    width: 100px;
    height: 100px;
    position: absolute;
    top: 100px;
    left: 100px;
  }

  .resizable .resizers {
    width: 100%;
    height: 100%;
    border: 3px dashed #4286f4;
    box-sizing: border-box;
  }

  .resizable .resizers .resizer {
    width: 10px;
    height: 10px;
    border-radius: 50%;
    background: white;
    border: 3px solid #4286f4;
    position: absolute;
    z-index: 1000;
  }

  .resizable .resizers .resizer.top-right {
    right: -5px;
    top: -5px;
    cursor: nesw-resize;
  }
  .resizable .resizers .resizer.bottom-left {
    left: -5px;
    bottom: -5px;
    cursor: nesw-resize;
  }
  .resizable .resizers .resizer.bottom-right {
    right: -5px;
    bottom: -5px;
    cursor: nwse-resize;
  }
`;

const WgMainBox = styled.div`
  min-height: 100%;
  min-width: 100%;

  .wg-menu {
    position: absolute;
    top: 0;
    right: 0;
    width: 40px;
    height: 40px;
    border-bottom-left-radius: 20%;
    z-index: 20;
    background: #ccc url(${ImgMenu}) no-repeat center;
    background-size: 60%;
    transition: 0.3s;

    &:hover {
      transition: 0.3s;
      cursor: pointer;
      background: #4da6ff url(${ImgMenu}) no-repeat center;
      background-size: 60%;
    }
  }
`;

const WgMenu = styled.div`
  display: flex;
  flex-direction: column;
  min-width: 200px;
  min-height: 140px;
  padding: 10px;
  box-shadow: 0 0 3px 0 rgba(0, 0, 0, 0.8);
  background: #fff;
  position: absolute;
  z-index: 100;
  top: 20px;
  right: -80px;

  .input {
    margin: 0 0 10px 0;
  }
`;

const WidgetMenu = ({
  widget,
  setIsWgMwnuOpen,
  setCurrentWidgetState,
  currentWidgetState,
}) => {
  const state = useSelector((state) => state.app);
  const { widgetsList } = state;
  const dispatch = useDispatch();

  return (
    <WgMenu
      onMouseDown={(e) => {
        e.stopPropagation();
        setCurrentWidgetState({
          ...currentWidgetState,
          draggable: false,
        });
      }}
      className="wg-menu-bar"
      onMouseOver={(e) => e.stopPropagation()}
    >
      <div
        onMouseDown={(e) => {
          e.stopPropagation();
          setCurrentWidgetState({
            ...currentWidgetState,
            draggable: false,
          });
        }}
        className="close-btn"
        onClick={() => {
          setIsWgMwnuOpen(false);
          setCurrentWidgetState({
            ...currentWidgetState,
            isMenuOpen: false,
          });
        }}
      >
        X
      </div>
      {widget.name} {get(widget, "clone") && "(Clone)"}
      <button
        className="remove-btn"
        onClick={() => {
          dispatch(
            setWidgetsList(widgetsList.filter((w) => w?.id !== widget?.id))
          );
        }}
      >
        Remove {widget.name}
      </button>
      <button
        className="remove-btn"
        onClick={() => {
          setIsWgMwnuOpen(false);
          setCurrentWidgetState({
            ...currentWidgetState,
            isMenuOpen: false,
          });

          dispatch(
            setWidgetsList([
              ...widgetsList,
              {
                id: new Date().getTime(),
                name: widget.name,
                top: 20,
                left: 20,
                clone: true,
                draggable: false,
              },
            ])
          );
        }}
      >
        Clone
      </button>
    </WgMenu>
  );
};

export const Dustbin = () => {
  const [currentWidgetState, setCurrentWidgetState] = useState({
    isActive: false,
    name: null,
    draggable: true,
    isMenuOpen: false,
  });

  const state = useSelector((state) => state.app);
  const { widgetsList, settings } = state;
  const { reportSettings } = settings;

  console.log(reportSettings, "reportSettings");

  const dispatch = useDispatch();

  const [{ canDrop, isOver }, drop] = useDrop({
    accept: "box",
    drop(item, monitor) {
      return { name: "Dustbin" };
    },

    collect: (monitor) => ({
      isOver: monitor.isOver(),
      canDrop: monitor.canDrop(),
    }),
  });

  const isActive = canDrop && isOver;
  let background = `url(${Grid})`;
  if (isActive) {
    background = `lightskyblue url(${Grid})`;
  } else if (canDrop) {
    background = `lightgreen url(${Grid})`;
  }

  const [activeDrags, setActiveDrags] = useState(0);

  function onStart() {
    return currentWidgetState?.draggable;
  }

  function onStop() {
    setActiveDrags(activeDrags);
  }

  const dragHandlers = { onStart, onStop };

  return (
    <DnDBox reportSettings={reportSettings}>
      <div
        style={{
          position: "relative",
          width: "fit-content",
          margin: "10px",
          background: `rgba(${reportSettings?.basic?.backgroundColor?.rgb.r}, ${reportSettings?.basic?.backgroundColor?.rgb.g},${reportSettings?.basic?.backgroundColor?.rgb.b}, 0.6)`,
        }}
      >
        <ReactResizeDetector
          handleWidth
          handleHeight
          onResize={(width, height) => {
            dispatch(
              setSettings({
                ...settings,
                reportSettings: {
                  ...settings.reportSettings,
                  position: {
                    ...settings?.reportSettings?.position,
                    width: width + 4,
                    height: height + 4,
                  },
                },
              })
            );
          }}
        >
          {({ width, height }) => {
            return (
              <div
                className="sidebar"
                ref={drop}
                style={{
                  background,
                  position: "relative",
                  overflow: "auto",
                  textAlign: "center",
                  width: `${reportSettings.position.width}px`,
                  height: `${reportSettings.position.height}px`,
                  minHeight: `${reportSettings.position.minHeight}px`,
                  minWidth: `${reportSettings.position.minWidth}px`,
                  border:
                    reportSettings?.basic?.border === "None"
                      ? "2px solid transparent"
                      : `2px ${reportSettings?.basic?.border || "solid"} ${
                          reportSettings?.basic?.color?.hex || "#ccc"
                        }`,
                }}
              >
                <div style={{ margin: "5px 0 0 0" }}>
                  W:{" "}
                  {get(
                    settings,
                    "reportSettings.position.width",
                    get(reportSettings, "basic.border") === "None"
                      ? width
                      : width + 4
                  )}{" "}
                  x H:{" "}
                  {get(
                    settings,
                    "reportSettings.position.height",
                    get(reportSettings, "basic.border") === "None"
                      ? height
                      : height + 4
                  )}
                </div>
                {/*<svg width="100%" height="100%" xmlns="http://www.w3.org/2000/svg">*/}
                {/*  <defs>*/}
                {/*    <pattern*/}
                {/*      id="smallGrid"*/}
                {/*      width="6"*/}
                {/*      height="6"*/}
                {/*      patternUnits="userSpaceOnUse"*/}
                {/*    >*/}
                {/*      <path*/}
                {/*        d="M 8 0 L 0 0 0 8"*/}
                {/*        fill="none"*/}
                {/*        stroke="gray"*/}
                {/*        stroke-width="0.5"*/}
                {/*      />*/}
                {/*    </pattern>*/}
                {/*    <pattern*/}
                {/*      id="grid"*/}
                {/*      width="60"*/}
                {/*      height="60"*/}
